CREATE TABLE IF NOT EXISTS `hot_product` (
  `p_type` int(10) NOT NULL COMMENT '商品类型',
  `p_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '商品编号',
  `p_name` varchar(40) NOT NULL COMMENT '商品名称',
  `p_image` varchar(100) NOT NULL COMMENT '商品图片',
  `p_description` varchar(1000) NOT NULL COMMENT '商品描述',
  `is_news` int(100) NOT NULL DEFAULT '0' COMMENT '是否为新闻动态',
  `p_cailiao` varchar(1000) NOT NULL COMMENT '材料',
  `p_buzhou` varchar(2000) NOT NULL COMMENT '料理步骤',
  `p_count` int(100) NOT NULL DEFAULT '0' COMMENT '商品点击次数',
  `p_time` datetime NOT NULL COMMENT '商品时间',
  PRIMARY KEY (`p_id`),
  KEY `p_type` (`p_type`),
  KEY `p_type_2` (`p_type`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='热销产品' AUTO_INCREMENT=78 ;

