--
-- 表的结构 `join_investment`
--

CREATE TABLE IF NOT EXISTS `join_investment` (
  `name` varchar(6) NOT NULL COMMENT '加盟人',
  `telphone` varchar(11) NOT NULL COMMENT '加盟电话',
  `address` varchar(50) NOT NULL COMMENT '地址',
  `email` varchar(40) NOT NULL COMMENT '邮箱',
  `apply_time` datetime NOT NULL COMMENT '申请时间',
  `is_tel` varchar(100) NOT NULL COMMENT '是否联系',
  `is_join` varchar(100) NOT NULL COMMENT '是否加盟',
  PRIMARY KEY (`name`),
  KEY `telphone` (`telphone`),
  KEY `telphone_2` (`telphone`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='招商加盟';

